 */
$installer = $this;
$installer->startSetup();

$installer->run("
TRUNCATE TABLE {$this->getTable('core_email_template')};

INSERT INTO `core_email_template` (`template_code`, `template_text`, `template_styles`, `template_type`, `template_subject`, `template_sender_name`, `template_sender_email`, `added_at`, `modified_at`, `orig_template_code`, `orig_template_variables`) VALUES  
('客户注册', '<style type=\"text/css\">\r\nbody,td { color:#2f2f2f; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; }\r\n</style>\r\n\r\n<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n        <tr>\r\n            <td align=\"center\" valign=\"top\">\r\n                <!-- [ header starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <a href=\"{{store url=\"\"}}\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"Makingware\"  style=\"margin-bottom:10px;\" border=\"0\"/></a></td>\r\n                    </tr>\r\n                </table>\r\n\r\n                <!-- [ middle starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n						<p><strong>您好，{{var customer.name}}</strong>：<br/>\r\n                            欢迎光临 <a href=\"{{store url=\"\"}}\" style=\"color:#1E7EC8;\">{{config path=\"general/store_information/name\"}}</a>。登录请点击页面顶部的<a href=\"{{store url=\"customer/account/login\"}}\" style=\"color:#1E7EC8;\">登录</a>或<a href=\"{{store url=\"customer/account/\"}}\" style=\"color:#1E7EC8;\">我的帐号</a>，并输入电子邮件地址和密码。</p>\r\n\r\n                            <p style=\"border:1px solid #BEBCB7; padding:13px 18px; background:#F8F7F5; \">\r\n                                请用下面的帐号登录：<br/>\r\n                                电子邮件: {{var customer.email}}<br/>\r\n                                密码: {{var customer.password}}</p>\r\n\r\n                            <p>登录您的帐号后，您可以：</p>\r\n                            <ul>\r\n                                <li>购物时结帐更迅速</li>\r\n                                <li>查看订单状态</li>\r\n                                <li>查看历史订单</li>\r\n                                <li>修改账户信息</li>\r\n                                <li>修改密码</li>\r\n                                <li>保存多个地址(给亲戚朋友购物！)</li>\r\n                            </ul>\r\n                            <p>有关账户或购物的任何疑问，请联系<a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\" style=\"color:#1E7EC8;\">{{config path=\"trans_email/ident_general/email\"}}</a>或打电话 {{config path=\"general/store_information/phone\"}}</p>\r\n                            <p>多谢惠顾！</p>\r\n                        </td>\r\n                    </tr>\r\n                </table>\r\n            </td>\r\n        </tr>\r\n    </table>\r\n</div>', '', 2, '您好, {{var customer.name}}!', NULL, NULL, NOW(), NOW(), 'customer_create_account_email_template', ''),
('重置密码', '<style type=\"text/css\">body,td { color:#2f2f2f; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; }</style>\r\n\r\n<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n        <tr>\r\n            <td align=\"center\" valign=\"top\">\r\n                <!-- [ header starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <p><a href=\"{{store url=\"\"}}\" style=\"color:#1E7EC8;\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"Makingware\" border=\"0\"/></a></p></td>\r\n                    </tr>\r\n                </table>\r\n\r\n                <!-- [ middle starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n<p><strong>您好，{{var customer.name}}</strong>：<br/>\r\n您新的密码是：{{var customer.password}}</p>\r\n<p>您随时可以登录<a href=\"{{store url=\"customer/account/\"}}\" style=\"color:#1E7EC8;\">您的账户</a>修改密码。<p>\r\n<p>多谢惠顾！<br/><strong><a href=\"{{store url=\"\"}}\" style=\"color:#1E7EC8;\">{{config path=\"general/store_information/name\"}}</a> </strong></p>\r\n                        </td>\r\n                    </tr>\r\n                </table>\r\n\r\n            </td>\r\n        </tr>\r\n    </table>\r\n</div>', '', 2, '{{var customer.name}}找回密码', NULL, NULL, NOW(), NOW(), 'customer_password_forgot_email_template', ''),
('加入收藏', '<style type=\"text/css\">\r\n    body,td { color:#2f2f2f; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; }\r\n</style>\r\n<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n        <tr>\r\n            <td align=\"center\" valign=\"top\">\r\n            <!-- [ header starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <p><a href=\"{{store url=\"\"}}\" style=\"color:#1E7EC8;\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"Makingware\" border=\"0\"/></a></p></td>\r\n                    </tr>\r\n                </table>\r\n\r\n            <!-- [ middle starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                        <p>您好，<br/>\r\n                        请看看我在 <a href=\"{{store url=\"\"}}\" style=\"color:#1E7EC8;\">{{config path=\"general/store_information/name\"}}</a>的收藏夹。</p>\r\n\r\n<p>{{var message}}</p>\r\n\r\n                        {{var items}}\r\n\r\n                        <br/>\r\n\r\n<p><strong><a href=\"{{var addAllLink}}\" style=\"color:#DC6809;\">添加所有商品到购物车</a></strong> | <strong><a href=\"{{var viewOnSiteLink}}\" style=\"color:#1E7EC8;\">查看收藏夹中所有商品</a></strong></p>\r\n\r\n                        <p>多谢惠顾！<br/><strong>{{var customer.name}}</strong></p>\r\n\r\n\r\n                        </td>\r\n                    </tr>\r\n                </table>\r\n\r\n            </td>\r\n        </tr>\r\n    </table>\r\n    </div>', '', 2, '{{var customer.name}}的收藏', NULL, NULL, NOW(), NOW(), 'wishlist_email_email_template', ''),
('客户新订单', '<style type=\"text/css\">\r\n    body,td { color:#2f2f2f; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; }\r\n</style>\r\n\r\n<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n<tr>\r\n    <td align=\"center\" valign=\"top\">\r\n        <!-- [ header starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n            <tr>\r\n                <td valign=\"top\"><a href=\"{{store url=\"\"}}\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"Makingware\"  style=\"margin-bottom:10px;\" border=\"0\"/></a></td>\r\n            </tr>\r\n        </table>\r\n        <!-- [ middle starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n            <tr>\r\n                <td valign=\"top\">\r\n                    <p>\r\n                        <strong>您好，{{var order.getCustomerName()}}</strong>,<br/>\r\n                        感谢您在{{var order.getStoreGroupName()}}的订购。\r\n                        发货之后我们会立即给您发送邮件，附带查询订单的链接。\r\n                        您可以通过<a href=\"{{store url=\"customer/account/\"}}\" style=\"color:#1E7EC8;\">登录您的帐户</a>查看订单状态.\r\n                        关于订单如有任何疑问，请发邮件至<a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\" style=\"color:#1E7EC8;\">{{config path=\"trans_email/ident_general/email\"}}</a> 或拨打电话 <span class=\"nobr\">{{config path=\"general/store_information/phone\"}}</span> 周一至周五，早上8点至下午5点。\r\n                    </p>\r\n                    <p>您的订单确认如下。感谢您的惠顾。</p>\r\n\r\n                    <h3 style=\"border-bottom:2px solid #eee; font-size:1.05em; padding-bottom:1px; \">您的订单#{{var order.increment_id}} <small>(提交于 {{var order.getCreatedAtFormated(\'long\')}})</small></h3>\r\n                    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\r\n                        <thead>\r\n                        <tr>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">支付方式：</th>\r\n                        </tr>\r\n                        </thead>\r\n                        <tbody>\r\n                        <tr>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var payment_html}}\r\n                            </td>\r\n                        </tr>\r\n                        </tbody>\r\n                    </table>\r\n                    <br/>\r\n                    {{depend order.getIsNotVirtual()}}\r\n                    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\r\n                        <thead>\r\n                        <tr>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">配送地址：</th>\r\n                            <th width=\"3%\"></th>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">配送方式：</th>\r\n                        </tr>\r\n                        </thead>\r\n                        <tbody>\r\n                        <tr>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var order.getShippingAddress().format(\'html\')}}\r\n                            </td>\r\n                            <td>&nbsp;</td>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var order.getShippingDescription()}}\r\n                            </td>\r\n                        </tr>\r\n                        </tbody>\r\n                    </table>\r\n                    <br/>\r\n                    {{/depend}}\r\n                    {{layout handle=\"sales_email_order_items\" order=\$order}}\r\n\r\n                    {{var items_html}}\r\n                    <br/>\r\n                    {{var order.getEmailCustomerNote()}}\r\n                    <p>多谢惠顾！<br/><strong>{{var order.getStoreGroupName()}}</strong></p>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n</table>\r\n</div>', '', 2, '{{var order.getStoreGroupName()}}: New Order # {{var order.increment_id}}', NULL, NULL, NOW(), NOW(), 'sales_email_order_template', ''),
('游客的新订单', '<style type=\"text/css\">\r\n    body,td { color:#2f2f2f; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; }\r\n</style>\r\n\r\n<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n<tr>\r\n    <td align=\"center\" valign=\"top\">\r\n        <!-- [ header starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n            <tr>\r\n                <td valign=\"top\"><a href=\"{{store url=\"\"}}\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"Makingware\"  style=\"margin-bottom:10px;\" border=\"0\"/></a></td>\r\n            </tr>\r\n        </table>\r\n        <!-- [ middle starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n            <tr>\r\n                <td valign=\"top\">\r\n                    <p>\r\n                        <strong>您好，{{var order.getShippingAddress().getName()}}</strong>,<br/>\r\n                        感谢您在{{var order.getStoreGroupName()}}的订购。\r\n                        发货之后我们会立即给您发送邮件，附带查询订单的链接。\r\n                        关于订单如有任何疑问，请发邮件至 <a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\" style=\"color:#1E7EC8;\">{{config path=\"trans_email/ident_general/email\"}}</a> 或拨打电话 <span class=\"nobr\">{{config path=\"general/store_information/phone\"}}</span> 周一至周五，早上8点至下午5点。\r\n                    </p>\r\n                    <p>您的订单确认如下。感谢您的惠顾。</p>\r\n\r\n                    <h3 style=\"border-bottom:2px solid #eee; font-size:1.05em; padding-bottom:1px; \">您的订单#{{var order.increment_id}} <small>(提交于{{var order.getCreatedAtFormated(\'long\')}})</small></h3>\r\n                    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\r\n                        <thead>\r\n                        <tr>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">支付方式：</th>\r\n                        </tr>\r\n                        </thead>\r\n                        <tbody>\r\n                        <tr>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var payment_html}}\r\n                            </td>\r\n                        </tr>\r\n                        </tbody>\r\n                    </table>\r\n                    <br/>\r\n                    {{depend order.getIsNotVirtual()}}\r\n                    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"100%\">\r\n                        <thead>\r\n                        <tr>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">配送地址：</th>\r\n                            <th width=\"3%\"></th>\r\n                            <th align=\"left\" width=\"48.5%\" bgcolor=\"#d9e5ee\" style=\"padding:5px 9px 6px 9px; border:1px solid #bebcb7; border-bottom:none; line-height:1em;\">配送方式：</th>\r\n                        </tr>\r\n                        </thead>\r\n                        <tbody>\r\n                        <tr>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var order.getShippingAddress().format(\'html\')}}\r\n                            </td>\r\n                            <td>&nbsp;</td>\r\n                            <td valign=\"top\" style=\"padding:7px 9px 9px 9px; border:1px solid #bebcb7; border-top:0; background:#f8f7f5;\">\r\n                                {{var order.getShippingDescription()}}\r\n                            </td>\r\n                        </tr>\r\n                        </tbody>\r\n                    </table>\r\n                    <br/>\r\n                    {{/depend}}\r\n                    {{layout handle=\"sales_email_order_items\" order=\$order}}\r\n                    <br/>\r\n                    {{var order.getEmailCustomerNote()}}\r\n                    <p>多谢惠顾！<br/><strong>{{var order.getStoreGroupName()}}</strong></p>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n</table>\r\n</div>', '', 2, '{{var order.getStoreGroupName()}}: New Order # {{var order.increment_id}}', NULL, NULL, NOW(), NOW(), 'sales_email_order_guest_template', '');

INSERT INTO `core_email_template` (`template_code`, `template_text`, `template_styles`, `template_type`, `template_subject`, `template_sender_name`, `template_sender_email`, `added_at`, `modified_at`, `orig_template_code`, `orig_template_variables`) VALUES 
('客户订单更新', '<style type=\"text/css\">body,td { color:#2f2f2f; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; }</style>\r\n<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n<tr>\r\n    <td align=\"center\" valign=\"top\">\r\n        <!-- [ header starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n        <tr>\r\n            <td valign=\"top\"><a href=\"{{store url=\"\"}}\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"Makingware\"  style=\"margin-bottom:10px;\" border=\"0\"/></a></td>\r\n        </tr>\r\n        </table>\r\n\r\n        <!-- [ middle starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n        <tr>\r\n            <td valign=\"top\">\r\n                <p>\r\n                    <strong>您好，{{var order.getCustomerName()}}</strong>,<br/>\r\n                    您的订单# {{var order.increment_id}}已<br/>\r\n                    <strong>{{var order.getStatusLabel()}}</strong>.\r\n                </p>\r\n                <p>您可以通过<a href=\"{{store url=\"customer/account/\"}}\" style=\"color:#1E7EC8;\">登录您的帐户</a>查看订单状态。</p>\r\n                <p>{{var comment}}</p>\r\n                <p>\r\n                    如有任何疑问，请发邮件至\r\n                    <a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\" style=\"color:#1E7EC8;\">{{config path=\"trans_email/ident_general/email\"}}</a>\r\n                    或拨打电话 {{config path=\"general/store_information/phone\"}}\r\n                </p>\r\n                <p>多谢惠顾！<br/><strong>{{var order.getStoreGroupName()}}</strong></p>\r\n            </td>\r\n        </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n</table>\r\n</div>', '', 2, '{{var order.getStoreGroupName()}}: Order # {{var order.increment_id}} update', NULL, NULL, NOW(), NOW(), 'sales_email_order_comment_template', ''),
('游客的订单更新', '<style type=\"text/css\">body,td { color:#2f2f2f; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; }</style>\r\n<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:12px/1.5em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n<tr>\r\n    <td align=\"center\" valign=\"top\">\r\n        <!-- [ header starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n        <tr>\r\n            <td valign=\"top\"><a href=\"{{store url=\"\"}}\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"Makingware\"  style=\"margin-bottom:10px;\" border=\"0\"/></a></td>\r\n        </tr>\r\n        </table>\r\n\r\n        <!-- [ middle starts here] -->\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n        <tr>\r\n            <td valign=\"top\">\r\n                <p>\r\n                    <strong>您好，{{var shipping.getName()}}</strong>,<br/>\r\n                    您的订单# {{var order.increment_id}}已<br/>\r\n                    <strong>{{var order.getStatusLabel()}}</strong>.\r\n                </p>\r\n                <p>{{var comment}}</p>\r\n                <p>\r\n                    如有任何疑问，请发邮件至\r\n                    <a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\" style=\"color:#1E7EC8;\">{{config path=\"trans_email/ident_general/email\"}}</a>\r\n                    或拨打电话 {{config path=\"general/store_information/phone\"}}\r\n                </p>\r\n                <p>多谢惠顾！<br/><strong>{{var order.getStoreGroupName()}}</strong></p>\r\n            </td>\r\n        </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n</table>\r\n</div>', '', 2, '{{var order.getStoreGroupName()}}: Order # {{var order.increment_id}} update', NULL, NULL, NOW(), NOW(), 'sales_email_order_comment_guest_template', ''),
